                            Расположение ~ (^ / piwigo) (/ \ d) (/.*/ pwg_format /.*) {
                                    # На данный момент просто удалите идентификатор изображения с URI и извлеките изображение
                                    try_files $ 1 $ 3 = 404;
                            }
                            # Маршрут к Piwigo_Privacy / get.php должен быть изменен на это - предупреждение, это полностью обходит привазность_plugin.
                            Расположение ~ (^ / piwigo) (/ \ d ///(??pwg_format/)[^/]*) {
